mapper.update:
UPDATE	TCHR_INFO
SET		MBR_ID   = #{memberId}

		, COMP_NM  = #{companyName}


		, BUIS_NMB = #{businessNumber}


		, ANNUAL   = #{annual}

WHERE	MBR_ID   = #{memberId}

UPDATE	TCHR_BOOK
SET		TB_ID   = #{id}

		, BOOK_NM  = #{bookName}


		, BOOK_COMP = #{bookCompany}

WHERE	TB_ID   = #{id}

UPDATE	TCHR_PROJ_HTR
SET		TPH_ID   = #{id}

		, STR_DATE  = #{startDate}


		, END_DATE = #{endDate}


		, PROJ_NM   = #{projectName}


		, PROJ_LOC   = #{projectLocation}

WHERE	TPH_ID   = #{id}

UPDATE	TCHR_EDU_HTR
SET		TEH_ID   = #{id}

		, STR_DATE  = #{startDate}


		, END_DATE = #{endDate}


		, EDU_NM   = #{educationName}


		, EDU_LOC   = #{educationLocation}

WHERE	TEH_ID   = #{id}

mapper.delete:
DELETE
FROM	
		TCHR_BOOK


		TCHR_EDU_HTR


		TCHR_PROJ_HTR

WHERE	1=1

AND		TB_ID = #{id}


AND		TPH_ID = #{id}


AND		TEH_ID = #{id}

mapper.insert:
