mapper.select:
SELECT COUNT(1) FROM SYSTEM.BOARD , SYSTEM.USERS WHERE BOARD.USER_ID = USERS.USER_ID
AND BOARD.SUBJECT = #{searchKeyword}
ORDER BY ID DESC

SELECT ID , SUBJECT , CONTENT , USER_ID , TO_CHAR(CRT_DT, 'YYYY-MM-DD HH24:MI:SS') CRT_DT , TO_CHAR(MDFY_DT, 'YYYY-MM-DD HH24:MI:SS') MDFY_DT , HIT , RECOMMEND , USER_NAME , FILE_NAME FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT ID , SUBJECT , CONTENT , BOARD.USER_ID , BOARD.CRT_DT , BOARD.MDFY_DT , HIT , RECOMMEND , USERS.USER_NAME , BOARD.FILE_NAME FROM SYSTEM.BOARD , SYSTEM.USERS WHERE BOARD.USER_ID = USERS.USER_ID
AND BOARD.SUBJECT = #{searchKeyword}
ORDER BY ID DESC ) A WHERE ROWNUM <= #{endNumber} ) WHERE RNUM >= #{startNumber}

mapper.update:
UPDATE	SYSTEM.BOARD
SET		HIT = HIT + 1
WHERE	ID = #{id}

UPDATE	SYSTEM.BOARD
SET		RECOMMEND = RECOMMEND + 1
WHERE	ID = #{id}

UPDATE	SYSTEM.BOARD
SET		SUBJECT = #{subject}
		, CONTENT = #{content}
		, FILE_NAME = #{fileName}
WHERE	ID = #{id}
AND		USER_ID = #{userId}
